#!/bin/python3


def bathroom_security(keys):
    position = [2, 0]
    keypad = [
        [None, None, 1, None, None],
        [None, 2, 3, 4, None],
        [5, 6, 7, 8, 9],
        [None, "A", "B", "C", None],
        [None, None, "D", None, None],
    ]
    solution = ""
    for key in keys:
        for delta in key:
            temp_position = list(position)
            if delta == "U":
                position[0] = max((position[0] - 1), 0)
            elif delta == "R":
                position[1] = min((position[1] + 1), 4)
            elif delta == "D":
                position[0] = min((position[0] + 1), 4)
            elif delta == "L":
                position[1] = max((position[1] - 1), 0)
            if keypad[position[0]][position[1]] is None:
                position = list(temp_position)
        solution += str(keypad[position[0]][position[1]])
    return solution


sample_1 = ["ULL", "RRDDD", "LURDL", "UUUUD"]
print("Sample 1: {}".format(bathroom_security(sample_1)))

